const { TestWatcher } = require("jest");

const calculadora = required("./calculadora");

test('deve somar 1 + 1 = 2', () => {
    const resultado  = calculadora.soma(1, 1);
    expect(resultado).toBe(2);

})
test('deve somar 26 + 34 = 60', () => {
    const resultado  = calculadora.soma(26, 34);
    expect(resultado).toBe(60);
}) 
test('deve somar 100 + 100', () => {
    const resultado = calculadora.soma(100,100);
    expect(resultado).toBe(200);
})  
test('deve dividir 25 / 5 = 5', () => {
    const resultado = calculadora.divide(25,5);
    expect(resultado).toBe(5);
})
test('deve dividir 192 / 2 = 96', () => {
    const resultado  = calculadora.divide(192, 2);
    expect(resultado).toBe(96);

})
test('deve subtrair 50 - 3 = 47', () => {
    const resultado  = calculadora.subtrai(50, 3);
    expect(resultado).toBe(47);

})
test('deve subtrair 4 - 10 = -6', () => {
    const resultado  = calculadora.subtrai(4, 10);
    expect(resultado).toBe(-6);

})
test('deve multiplicar 7 * 9 = 63', () => {
    const resultado  = calculadora.multiplica(7, 9);
    expect(resultado).toBe(63);

})
test('deve multiplicar 9 * 100 = 900', () => {
    const resultado  = calculadora.multiplica(9, 100);
    expect(resultado).toBe(900);

})

